package com.produban.ejercicio008;import java.util.ArrayList;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;@Componentpublic class TaquillaFactory {	@Autowired	private TaquillaService taquillaService;		@Autowired	private TaquillaDescuentoGrupoService taquillaDescuentoGrupoService;		@Autowired	private TaquillaDiaEspectadorService taquillaDiaEspectadorService;		public TaquillaService getInstance(int tipoTaquilla) {		TaquillaService taquillaResultado = null;				if (tipoTaquilla == TaquillaService.TAQUILLA_NORMAL) {			taquillaResultado = taquillaService;		} else if (tipoTaquilla == TaquillaService.TAQUILLA_DESCUENTO_GRUPO) {			taquillaResultado = taquillaDescuentoGrupoService;		} else if (tipoTaquilla == TaquillaService.TAQUILLA_DIA_ESPECTADOR) {			taquillaResultado = taquillaDiaEspectadorService;		} 		if (taquillaResultado != null) {			return taquillaResultado;		}		throw new RuntimeException("Tipo de taquillaService no contemplado: " + tipoTaquilla);	}	public TaquillaService getTaquilla() {		return taquillaService;	}	public void setTaquilla(TaquillaService taquillaService) {		this.taquillaService = taquillaService;	}	public TaquillaDescuentoGrupoService getTaquillaDescuentoGrupo() {		return taquillaDescuentoGrupoService;	}	public void setTaquillaDescuentoGrupo(			TaquillaDescuentoGrupoService taquillaDescuentoGrupoService) {		this.taquillaDescuentoGrupoService = taquillaDescuentoGrupoService;	}	public TaquillaDiaEspectadorService getTaquillaDiaEspectador() {		return taquillaDiaEspectadorService;	}	public void setTaquillaDiaEspectador(TaquillaDiaEspectadorService taquillaDiaEspectadorService) {		this.taquillaDiaEspectadorService = taquillaDiaEspectadorService;	}	}